mapper.select:
SELECT p.id, p.name, p.description, p.creator_id, cs.name AS creator_name, p.status, p.review_status, p.class_id, c.name AS class_name, p.start_date, p.end_date, p.created_at, pm.role FROM projects p JOIN project_members pm ON pm.project_id = p.id JOIN students s ON pm.student_id = s.id JOIN accounts a ON s.account_id = a.id JOIN students cs ON p.creator_id = cs.id JOIN classes c On p.class_id = c.id
a.id=#{id}
and p.creator_id=#{creatorId}
and p.class_id=#{classId}
and p.status=#{status}
and p.review_status=#{reviewStatus}
ORDER BY p.created_at DESC
