mapper.select:
SELECT t.id, t.name, t.description, t.project_id, p.name as project_name, t.assigned_to, cs.name as assigner, t.due_date, t.status, tm.role, tm.assigned_at, tp.progress, tp.description as progress_description FROM tasks t LEFT JOIN task_members tm ON tm.task_id = t.id LEFT JOIN ( SELECT tp1.* FROM task_progress tp1 WHERE tp1.update_at = ( SELECT MAX(tp2.update_at) FROM task_progress tp2 WHERE tp2.task_id = tp1.task_id ) ) tp ON tp.task_id = t.id LEFT JOIN students cs ON cs.id = t.assigned_to LEFT JOIN projects p ON p.id = t.project_id JOIN students s ON s.id = tm.student_id JOIN accounts a ON a.id = s.account_id
a.id=#{id}
and t.name=#{name}
and t.assigned_to=#{assignedTo}
and t.due_date=#{dueDate}
and t.status=#{status}
ORDER BY t.created_at DESC
