mapper.update:
update accounts a
JOIN students s ON s.account_id = a.id

    s.student_id=s.student_id

        , a.password=#{password}


        , s.class_number=#{classNumber}


        , s.email=#{email}


        , s.phone=#{phone}



    a.id=#{accountId}

update accounts a
JOIN teachers t ON t.account_id = a.id

    t.teacher_id=t.teacher_id

        , a.password=#{password}


        , t.email=#{email}


        , t.phone=#{phone}



    a.id=#{accountId}
